import { request } from '@/utils/request';
export const posts = async (params, callback) => {
  return await request('get', '/api/posts', params, callback);
};
export const indexPosts = async (search, search_fields, page, size, with_tags, without_tags, callback) => {
  return await posts({
    search,
    search_fields: search_fields.join(','),
    offset: (page - 1) * size,
    number: size,
    with_tags: with_tags.map(item => item.id).join(','),
    without_tags: without_tags.map(item => item.id).join(',')
  }, callback);
};
