import "antd/lib/notification/style/css";
import _notification from "antd/lib/notification";
export default function ShowNotification(res) {
  if (res.success) {
    _notification.success({
      message: res.title,
      description: res.content
    });
  } else {
    _notification.error({
      message: res.title,
      description: res.content
    });
  }

  return res.success;
}
export function H5Notification(msg) {
  if (typeof document !== 'undefined') {
    Notification.requestPermission();
    new Notification('通知', {
      body: msg,
      icon: '/static/img/logo_196x196.png'
    });
  }
}
